import React from 'react';
import './Commands.css'
import { Card, Button, Container } from 'semantic-ui-react'
import commands from './Command';

let STR = ''

commands.forEach(({ header, description }) => STR += `${header} : ${description}\n`)
console.log(STR)

function Copy() {
  navigator.clipboard.writeText(STR)
  alert('내용을 모두 복사하였습니다!')
}

function Commands() {
  return (
    <div className='Commands'>
      <Button onClick={Copy} className='TextCopy'>답 복사하기</Button>
      <Container>
        <Card.Group items={commands} className='CommandsCard' />
      </Container>
    </div >
  );
}

export default Commands;
