const Society = [
    {
        "header": "이끔이",
        "description": "이정아"
    },
    {
        "header": "기록이",
        "description": "송찬우"
    },
    {
        "header": "기록이",
        "description": "이수진"
    },
    {
        "header": "칭찬이",
        "description": "김상훈"
    },
    {
        "header": "센스쟁이",
        "description": "이예원"
    },
    {
        "header": "우리가 탐구한 조례(안)",
        "description": "서울특별시 성북구 청소년대상 성범죄 예방 및 보호에 관한 조례안"
    },
    {
        "header": "제안한 지방 의회",
        "description": "서울특별시의회"
    },
    {
        "header": "제안한 이유 (최대 세줄)",
        "description": "최근 청소년 성범죄 발생률이 급격히 증가하여 청소년 대상 성범죄를 예방하기 위한 교육 실시 등 제도적 장치를 마련하고자 함."
    },
    {
        "header": "주요 내용 (가)",
        "description": "가. 조례의 목적과 정의를 규정함 (안 제1조 및 제2조)"
    },
    {
        "header": "주요 내용 (나)",
        "description": "나. 구청장과 구민의 책무를 규정함 (안 제3조 및 제4조)"
    },
    {
        "header": "주요 내용 (다)",
        "description": "다. 기본계획 수립에 관한 사항을 규정함(안 제5조)"
    },
    {
        "header": "주요 내용 (라)",
        "description": "라. 위원회 설치 및 청소년대상 성교육 전문기관의 설치.운영에 관한 사항을 규정함(안 제6조 및 제7조)"
    }
]

export default Society;
