import React from 'react';
import './Commands.css'
import { Card, Button, Container } from 'semantic-ui-react'
import commands from './Command';
import { } from 'react-clipboard.js'

const STR = commands.first.map(({ header, description }) => `${header} : ${description}`).join('\n')

function Copy() {
  try {
    navigator.clipboard.writeText(STR) // https만 가능
  } catch (_) {

  } finally {
    alert('내용을 모두 복사하였습니다!')
  }
}

function Commands() {
  return (
    <div className='Commands'>
      <Container>
        <Button onClick={Copy} className='TextCopy'>답 복사하기</Button>
        <Card.Group items={commands.first} className='CommandsCard' stackable />
      </Container>
    </div >
  );
}

export default Commands;
